import React, { useState } from 'react';
import { userService } from '../services/userService';
import { TextField, Button, Snackbar } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export function Sign() {
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (login.length < 5 || login.length > 10) {
      setError('Login must be 5-10 characters');
      return;
    }
    if (login === 'admin') {
      setError('Login "admin" is not allowed');
      return;
    }
    if (password.length > 8) {
      setError('Password must be 8 characters or less');
      return;
    }
    try {
      await userService.register({ login, password });
      navigate('/auth');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="sign">
      <h1>Registration</h1>
      <form onSubmit={handleSubmit} autoComplete="off">
        <TextField
          label="Login"
          value={login}
          onChange={(e) => setLogin(e.target.value)}
          fullWidth
          margin="normal"
          required
          autoComplete="off"
          error={login.length > 0 && (login.length < 5 || login.length > 10)}
          helperText={
            login.length > 0 && (login.length < 5 || login.length > 10)
              ? 'Login must be 5-10 characters'
              : ''
          }
        />
        <TextField
          label="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          fullWidth
          margin="normal"
          required
          autoComplete="off"
          error={password.length > 8}
          helperText={password.length > 8 ? 'Password must be 8 characters or less' : ''}
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={login.length < 5 || login.length > 10 || password.length > 8}
        >
          Register
        </Button>
      </form>
      {error && (
        <Snackbar
          open={!!error}
          autoHideDuration={6000}
          message={error}
          onClose={() => setError(null)}
        />
      )}
    </div>
  );
}
