import React from 'react';
import { Typography, Box, Button } from '@mui/material';

export default function Post({ post }) {
  return (
    <Box sx={{ border: '1px solid #ddd', padding: 2, marginBottom: 2, borderRadius: 4 }}>
      <Typography variant="h6">{post.title}</Typography>
      <Typography variant="body1">{post.content}</Typography>
      <Typography variant="caption">Автор: {post.user?.name || `User ${post.user_id}`}</Typography>
      <Box sx={{ marginTop: 1 }}>
        <Typography variant="caption">Лайков: {post.likes_count || 0}</Typography>
      </Box>
    </Box>
  );
}
