// frontend/src/services/userService.js
import axiosInstance from './axiosInstance';

export const userService = {
  getUsers(skip = 0, limit = 10, sortBy = null, search = null) {
    const params = new URLSearchParams({ skip, limit });
    if (sortBy) params.append('sort_by', sortBy);
    if (search) params.append('search', search);
    return axiosInstance.get(`/users/?${params.toString()}`).then((response) => response.data);
  },
  get(id) {
    return axiosInstance.get(`/users/${id}`).then((response) => response.data);
  },
  create(user) {
    return axiosInstance.post(`/users/`, user).then((response) => response.data);
  },
  update(id, user) {
    return axiosInstance.put(`/users/${id}`, user).then((response) => response.data);
  },
  delete(id) {
    return axiosInstance.delete(`/users/${id}`).then((response) => response.data);
  },
  login(user) {
    return axiosInstance.post(`/users/login`, user).then((response) => response.data);
  },
};
