import React, { useEffect, useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { userService } from '../services/userService';
import { useLoading } from '../contexts/LoadingContext';
import { Typography, Box } from '@mui/material';

export function Profile() {
  const { id } = useParams();
  const { user } = useContext(AuthContext);
  const [profileUser, setProfileUser] = useState(null);
  const [error, setError] = useState(null);
  const { setLoading } = useLoading();

  useEffect(() => {
    if (id) {
      setLoading(true);
      userService
        .get(id)
        .then((response) => {
          setProfileUser(response);
          setLoading(false);
        })
        .catch((err) => {
          setError(err.message);
          setLoading(false);
        });
    }
  }, [id, setLoading]);

  if (error) return <div className="error">Error: {error}</div>;

  return (
    <Box className="profile">
      <Typography variant="h4" gutterBottom>
        Profile
      </Typography>
      {profileUser ? (
        <>
          <Typography variant="body1"><strong>ID:</strong> {profileUser.id}</Typography>
          <Typography variant="body1"><strong>Name:</strong> {profileUser.name || 'N/A'}</Typography>
          <Typography variant="body1"><strong>Login:</strong> {profileUser.login || 'N/A'}</Typography>
        </>
      ) : (
        <Typography variant="body1">No user found</Typography>
      )}
    </Box>
  );
}
