import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { Button, IconButton, Menu, MenuItem } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import PeopleIcon from '@mui/icons-material/People';
import LoginIcon from '@mui/icons-material/Login';
import LogoutIcon from '@mui/icons-material/Logout';

export function Header() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    logout();
    navigate('/auth');
    handleClose();
  };

  return (
    <div className="header">
      <Button component={Link} to="/" startIcon={<HomeIcon />}>
        Home
      </Button>
      {user && (
        <Button component={Link} to="/users" startIcon={<PeopleIcon />}>
          Users
        </Button>
      )}
      {!user ? (
        <Button component={Link} to="/auth" startIcon={<LoginIcon />}>
          Login
        </Button>
      ) : (
        <>
          <IconButton
            onClick={handleMenu}
            className="header-user-button" // Добавляем кастомный класс
            style={{ marginLeft: 'auto' }}
          >
            {user.id}
          </IconButton>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleClose}
            className="header-menu" // Добавляем кастомный класс для меню
          >
            <MenuItem
              component={Link}
              to={`/users/${user.id}`}
              onClick={handleClose}
              className="header-menu-item"
            >
              Profile
            </MenuItem>
            <MenuItem onClick={handleClose} className="header-menu-item">
              Update
            </MenuItem>
            <MenuItem onClick={handleLogout} className="header-menu-item">
              <LogoutIcon style={{ marginRight: '0.5rem' }} />
              Logout
            </MenuItem>
          </Menu>
        </>
      )}
    </div>
  );
}
