import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { Button, IconButton, Menu, MenuItem } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

export function Header() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    logout();
    navigate('/auth');
    handleClose();
  };

  return (
    <div className="header">
      <Button component={Link} to="/" startIcon={<MenuIcon />}>
        Home
      </Button>
      {user && (
        <Button component={Link} to="/users" startIcon={<MenuIcon />}>
          Users
        </Button>
      )}
      {!user ? (
        <Button onClick={() => navigate('/auth')} startIcon={<MenuIcon />}>
          Login
        </Button>
      ) : (
        <div>
          <IconButton onClick={handleMenu} style={{ marginLeft: 'auto' }}>
            {user.id}
          </IconButton>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem component={Link} to={`/users/${user.id}`} onClick={handleClose}>
              Profile
            </MenuItem>
            <MenuItem onClick={handleClose}>Update</MenuItem>
            <MenuItem onClick={handleLogout}>Logout</MenuItem>
          </Menu>
        </div>
      )}
    </div>
  );
}
