import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import { Button, IconButton, Menu, MenuItem } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import PeopleIcon from '@mui/icons-material/People';
import LoginIcon from '@mui/icons-material/Login';
import LogoutIcon from '@mui/icons-material/Logout';

export function Header() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    logout();
    navigate('/auth');
    handleClose();
  };

  const buttonStyles = {
    color: '#fffde7', // Светло-желтый текст для лучшего контраста
    backgroundColor: 'rgba(0, 0, 0, 0.3)', // Чуть темнее фон
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    textTransform: 'none',
    fontSize: '1rem',
    marginRight: '1rem',
    '&:hover': {
      backgroundColor: 'rgba(0, 0, 0, 0.5)', // Еще темнее при наведении
    },
  };

  return (
    <div className="header">
      <Button component={Link} to="/" startIcon={<HomeIcon />} sx={buttonStyles}>
        Home
      </Button>
      {user && (
        <Button component={Link} to="/users" startIcon={<PeopleIcon />} sx={buttonStyles}>
          Users
        </Button>
      )}
      {!user ? (
        <Button component={Link} to="/auth" startIcon={<LoginIcon />} sx={buttonStyles}>
          Login
        </Button>
      ) : (
        <>
          <IconButton
            onClick={handleMenu}
            sx={{
              marginLeft: 'auto',
              color: '#fffde7',
              backgroundColor: 'rgba(0, 0, 0, 0.3)',
              padding: '0.5rem',
              fontSize: '1rem',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.5)',
              },
            }}
          >
            {user.id}
          </IconButton>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleClose}
            sx={{
              '& .MuiPaper-root': {
                backgroundColor: '#ffffff',
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)',
              },
            }}
          >
            <MenuItem
              component={Link}
              to={`/users/${user.id}`}
              onClick={handleClose}
              sx={{
                color: '#1976d2',
                fontSize: '1rem',
                padding: '0.5rem 1rem',
                '&:hover': {
                  backgroundColor: '#e3f2fd',
                },
              }}
            >
              Profile
            </MenuItem>
            <MenuItem
              onClick={handleClose}
              sx={{
                color: '#1976d2',
                fontSize: '1rem',
                padding: '0.5rem 1rem',
                '&:hover': {
                  backgroundColor: '#e3f2fd',
                },
              }}
            >
              Update
            </MenuItem>
            <MenuItem
              onClick={handleLogout}
              sx={{
                color: '#1976d2',
                fontSize: '1rem',
                padding: '0.5rem 1rem',
                '&:hover': {
                  backgroundColor: '#e3f2fd',
                },
              }}
            >
              <LogoutIcon style={{ marginRight: '0.5rem' }} />
              Logout
            </MenuItem>
          </Menu>
        </>
      )}
    </div>
  );
}
