// frontend/src/components/Users.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { userService } from '../services/userService';
import { useLoading } from '../contexts/LoadingContext';
import {
  Typography,
  Box,
  List,
  ListItem,
  ListItemText,
  Pagination,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
} from '@mui/material';

export function Users() {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const { setLoading } = useLoading();
  const [page, setPage] = useState(1);
  const [limit, setLimit] = useState(5); // По умолчанию 5 пользователей
  const [totalPages, setTotalPages] = useState(1);
  const [sortBy, setSortBy] = useState(''); // Сортировка
  const [search, setSearch] = useState(''); // Поиск
  const [searchInput, setSearchInput] = useState(''); // Временное значение для поиска

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const skip = (page - 1) * limit;
      const response = await userService.getUsers(skip, limit, sortBy || null, search || null);
      setUsers(response);

      
      const totalUsersResponse = await userService.getUsers(0, 1000); // Получаем всех пользователей для подсчёта
      const totalUsers = totalUsersResponse.length;
      setTotalPages(Math.ceil(totalUsers / limit));
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, [page, limit, sortBy, search]);

  const handleLimitChange = (event) => {
    setLimit(event.target.value);
    setPage(1); // Сбрасываем страницу на первую при изменении лимита
  };

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
    setPage(1); // Сбрасываем страницу на первую при изменении сортировки
  };

  const handleSearch = () => {
    setSearch(searchInput);
    setPage(1); // Сбрасываем страницу на первую при поиске
  };

  const handleSearchInputChange = (event) => {
    setSearchInput(event.target.value);
  };

  const handleClearSearch = () => {
    setSearch('');
    setSearchInput('');
    setPage(1);
  };

  if (error) return <div className="error">Ошибка: {error}</div>;

  return (
    <Box className="container">
        <Typography variant="h4" gutterBottom>
          Пользователи
        </Typography>

      {/* Фильтры и поиск */}
      <Box sx={{ display: 'flex', gap: 2, mb: 2, alignItems: 'center' }}>
        {/* Выбор количества пользователей на странице */}
        <FormControl sx={{ minWidth: 120 }}>
          <InputLabel>Пользователей на странице</InputLabel>
          <Select value={limit} onChange={handleLimitChange} label="Пользователей на странице">
            <MenuItem value={5}>5</MenuItem>
            <MenuItem value={20}>20</MenuItem>
            <MenuItem value={100}>100</MenuItem>
          </Select>
        </FormControl>

        {/* Сортировка */}
        <FormControl sx={{ minWidth: 120 }}>
          <InputLabel>Сортировать по</InputLabel>
          <Select value={sortBy} onChange={handleSortChange} label="Сортировать по">
            <MenuItem value="">Без сортировки</MenuItem>
            <MenuItem value="id">ID (по возрастанию)</MenuItem>
            <MenuItem value="id_desc">ID (по убыванию)</MenuItem>
            <MenuItem value="name">Имя (по возрастанию)</MenuItem>
            <MenuItem value="name_desc">Имя (по убыванию)</MenuItem>
          </Select>
        </FormControl>

        {/* Поиск */}
        <TextField
          label="Поиск по имени"
          value={searchInput}
          onChange={handleSearchInputChange}
          sx={{ flexGrow: 1 }}
        />
        <Button variant="contained" onClick={handleSearch}>
          Найти
        </Button>
        <Button variant="outlined" onClick={handleClearSearch}>
          Очистить
        </Button>
      </Box>

      {/* Список пользователей */}
      {users.length === 0 ? (
        <Typography>Пользователи не найдены</Typography>
      ) : (
        <List>
          {users.map((user) => (
            <ListItem key={user.id} component={Link} to={`/users/${user.id}`}>
              <ListItemText primary={user.name || `User ${user.id}`} secondary={`Логин: ${user.login}`} />
            </ListItem>
          ))}
        </List>
      )}

      {/* Пагинация */}
      {totalPages > 1 && (
        <Pagination
          count={totalPages}
          page={page}
          onChange={(event, value) => setPage(value)}
          sx={{ mt: 2, display: 'flex', justifyContent: 'center' }}
        />
      )}
    </Box>
  );
}
